"use strict";function addNewItem(e){e.preventDefault();var t=itemsForm.querySelector('[name="item"]').value;""!==t&&(itemsList.innerHTML+='<li><input type="checkbox" class="checkbox">'+t+"</li>",itemsForm.reset())}function toggleCheckbox(e){if(e.target.classList.contains("checkbox")){var t=e.target.parentNode;e.target.checked?t.style.textDecoration="line-through":t.style.textDecoration="none"}}function deleteCheckedItems(){document.querySelectorAll("li").forEach(function(e){e.querySelector(".checkbox").checked&&e.parentNode.removeChild(e)})}function areAnyCheckboxesChecked(){var e=document.querySelectorAll(".checkbox");return Array.from(e).some(function(e){return e.checked})}function changeButtonText(){areAnyCheckboxesChecked()?buttonContainer.innerHTML='<input type="submit" value="DELETE">':buttonContainer.innerHTML='<input type="submit" value="+ Add">'}var itemsList=document.querySelector(".list"),itemsForm=document.querySelector(".add-items"),buttonContainer=document.querySelector(".button-container");buttonContainer.innerHTML='<input type="submit" value="+ Add">',itemsForm.addEventListener("submit",function(e){e.preventDefault(),areAnyCheckboxesChecked()?deleteCheckedItems(e):addNewItem(e)}),itemsList.addEventListener("click",function(e){toggleCheckbox(e),changeButtonText()});